import { useEffect, useState } from "react";
import { User, Check, X, MessageSquare, Search, UserPlus } from "lucide-react";
import { api } from "@/services/api";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";

interface FriendListProps {
  renderMobileMenu?: React.ReactNode;
}

const FriendList = ({ renderMobileMenu }: FriendListProps) => {
  const [activeTab, setActiveTab] = useState<
    "online" | "all" | "pending" | "add"
  >("all");
  const [friends, setFriends] = useState<any[]>([]);
  const [pending, setPending] = useState<any[]>([]);
  const [addUsername, setAddUsername] = useState("");
  const { toast } = useToast();
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isSearching, setIsSearching] = useState(false);

  const handleSearch = async () => {
    if (!addUsername.trim()) return;
    setIsSearching(true);
    try {
      const results = await api.users.search(addUsername);
      setSearchResults(Array.isArray(results) ? results : []);
    } catch (error) {
      console.error("Search error:", error);
    } finally {
      setIsSearching(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [activeTab]);

  const fetchData = async () => {
    try {
      if (activeTab === "all" || activeTab === "online") {
        const data = await api.friends.list();
        setFriends(Array.isArray(data) ? data : []);
      }
      if (activeTab === "pending") {
        const data = await api.friends.pending();
        setPending(Array.isArray(data) ? data : []);
      }
    } catch (error) {
      console.error("Error fetching friends:", error);
    }
  };

  const handleSendRequest = async (targetUserId: string) => {
    try {
      await api.friends.request(targetUserId);
      toast({
        title: "Friend request sent",
        description: "They will see it in their pending tab.",
      });
      setAddUsername("");
      setSearchResults([]);
    } catch (error: any) {
      toast({
        title: "Error sending request",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleAccept = async (requestId: string) => {
    try {
      await api.friends.accept(requestId);
      toast({ title: "Friend request accepted" });
      fetchData();
    } catch (error: any) {
      toast({
        title: "Error accepting request",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <div className="flex-1 bg-notfox-light h-full flex flex-col">
      {/* Header */}
      <div className="h-10 sm:h-12 border-b border-border flex items-center px-2 sm:px-4 gap-2 sm:gap-4 bg-card/30 backdrop-blur-sm shrink-0">
        <div className="flex items-center gap-1 sm:gap-2 text-foreground font-semibold shrink-0">
          {renderMobileMenu}
          <User className="w-4 h-4 sm:w-5 sm:h-5 text-muted-foreground mr-1" />
          <span className="text-sm sm:text-base">Friends</span>
        </div>
        <div className="h-4 w-px bg-border mx-1 sm:mx-2" />
        <div className="flex gap-2 sm:gap-4 text-xs sm:text-sm overflow-x-auto no-scrollbar py-1">
          <button
            onClick={() => setActiveTab("online")}
            className={cn(
              "px-2 py-1 rounded transition-colors whitespace-nowrap",
              activeTab === "online"
                ? "text-foreground bg-white/10"
                : "text-muted-foreground hover:text-foreground hover:bg-white/5"
            )}
          >
            Online
          </button>
          <button
            onClick={() => setActiveTab("all")}
            className={cn(
              "px-2 py-1 rounded transition-colors whitespace-nowrap",
              activeTab === "all"
                ? "text-foreground bg-white/10"
                : "text-muted-foreground hover:text-foreground hover:bg-white/5"
            )}
          >
            All
          </button>
          <button
            onClick={() => setActiveTab("pending")}
            className={cn(
              "px-2 py-1 rounded transition-colors whitespace-nowrap flex items-center",
              activeTab === "pending"
                ? "text-foreground bg-white/10"
                : "text-muted-foreground hover:text-foreground hover:bg-white/5"
            )}
          >
            Pending
            {pending.length > 0 && (
              <span className="ml-1.5 px-1.5 py-0.5 rounded-full bg-destructive text-[9px] text-white">
                {pending.length}
              </span>
            )}
          </button>
          <button
            onClick={() => setActiveTab("add")}
            className={cn(
              "px-2 py-1 rounded transition-colors whitespace-nowrap",
              activeTab === "add"
                ? "text-success bg-success/10"
                : "text-success/70 hover:text-success hover:bg-success/5"
            )}
          >
            Add Friend
          </button>
        </div>
      </div>

      {/* Content */}
      <div className="p-4 flex-1 overflow-y-auto">
        {activeTab === "pending" && (
          <div className="space-y-2">
            <h3 className="text-xs font-bold text-muted-foreground uppercase mb-4">
              Pending Requests ΓÇö {pending.length}
            </h3>
            {pending.map((req) => (
              <div
                key={req.id}
                className="flex items-center justify-between p-3 rounded-lg hover:bg-notfox-darker/50 group border border-transparent hover:border-border/50"
              >
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 rounded-full bg-secondary flex items-center justify-center overflow-hidden">
                    {req.sender?.avatar_url ? (
                      <img src={req.sender.avatar_url} />
                    ) : (
                      req.sender?.username?.[0]
                    )}
                  </div>
                  <div>
                    <div className="font-bold text-foreground">
                      {req.sender?.display_name || req.sender?.username}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      Incoming Friend Request
                    </div>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleAccept(req.id)}
                    className="w-8 h-8 rounded-full bg-notfox-dark flex items-center justify-center hover:text-success hover:bg-notfox-black transition-colors"
                  >
                    <Check className="w-4 h-4" />
                  </button>
                  <button className="w-8 h-8 rounded-full bg-notfox-dark flex items-center justify-center hover:text-destructive hover:bg-notfox-black transition-colors">
                    <X className="w-4 h-4" />
                  </button>
                </div>
              </div>
            ))}
            {pending.length === 0 && (
              <div className="text-center text-muted-foreground py-10">
                No pending requests
              </div>
            )}
          </div>
        )}

        {/* All / Online */}
        {(activeTab === "all" || activeTab === "online") && (
          <div className="space-y-2">
            <h3 className="text-xs font-bold text-muted-foreground uppercase mb-4">
              {activeTab.toUpperCase()} FRIENDS ΓÇö {friends.length}
            </h3>
            {friends.map((friend) => (
              <div
                key={friend.friendship_id}
                className="flex items-center justify-between p-3 rounded-lg hover:bg-notfox-darker/50 group border border-transparent hover:border-border/50 cursor-pointer"
              >
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 rounded-full bg-secondary flex items-center justify-center overflow-hidden relative">
                    {friend.avatar_url ? (
                      <img
                        src={friend.avatar_url}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      friend.username?.[0]
                    )}
                    <div
                      className={`absolute bottom-0 right-0 w-3 h-3 border-2 border-notfox-light rounded-full ${
                        friend.status === "online"
                          ? "bg-success"
                          : "bg-muted-foreground"
                      }`}
                    ></div>
                  </div>
                  <div>
                    <div className="font-bold text-foreground">
                      {friend.display_name || friend.username}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {friend.status}
                    </div>
                  </div>
                </div>
                <div className="flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button
                    className="w-8 h-8 rounded-full bg-notfox-dark flex items-center justify-center hover:bg-notfox-black transition-colors"
                    title="Message"
                  >
                    <MessageSquare className="w-4 h-4" />
                  </button>
                </div>
              </div>
            ))}
            {friends.length === 0 && (
              <div className="text-center text-muted-foreground py-10">
                Wumpus is waiting on friends.
              </div>
            )}
          </div>
        )}

        {/* Add Friend */}
        {activeTab === "add" && (
          <div className="max-w-lg mx-auto mt-10">
            <h2 className="text-lg font-bold text-foreground mb-2">
              ADD FRIEND
            </h2>
            <p className="text-sm text-muted-foreground mb-4">
              You can add friends with their username.
            </p>
            <div className="flex gap-2">
              <div className="flex-1 bg-notfox-darker rounded-lg border border-border px-4 py-2 flex items-center focus-within:border-primary">
                <input
                  type="text"
                  className="bg-transparent border-none outline-none flex-1 text-foreground"
                  placeholder="Enter username to search..."
                  value={addUsername}
                  onChange={(e) => setAddUsername(e.target.value)}
                  onKeyDown={(e) => e.key === "Enter" && handleSearch()}
                />
                <button
                  onClick={handleSearch}
                  className="ml-2 text-muted-foreground hover:text-foreground"
                >
                  <Search className="w-5 h-5" />
                </button>
              </div>
            </div>

            {/* Search Results */}
            <div className="mt-8 space-y-2">
              {addUsername && searchResults.length > 0 && (
                <h3 className="text-xs font-bold text-muted-foreground uppercase mb-2">
                  Search Results
                </h3>
              )}
              {searchResults.map((user) => (
                <div
                  key={user.id}
                  className="flex items-center justify-between p-3 rounded-lg hover:bg-notfox-darker/50 border border-transparent hover:border-border/50"
                >
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-full bg-secondary flex items-center justify-center overflow-hidden">
                      {user.avatar_url ? (
                        <img
                          src={user.avatar_url}
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        user.username?.[0].toUpperCase()
                      )}
                    </div>
                    <div>
                      <div className="font-bold text-foreground">
                        {user.display_name || user.username}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        @{user.username}
                      </div>
                    </div>
                  </div>
                  <button
                    onClick={() => handleSendRequest(user.id)}
                    className="p-2 rounded-full bg-primary hover:bg-primary/90 text-white transition-colors"
                    title="Send Friend Request"
                  >
                    <UserPlus className="w-4 h-4" />
                  </button>
                </div>
              ))}
              {isSearching && (
                <div className="text-center text-muted-foreground py-4">
                  Searching...
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FriendList;

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default: "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary: "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive: "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

export interface BadgeProps extends React.HTMLAttributes<HTMLDivElement>, VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return <div className={cn(badgeVariants({ variant }), className)} {...props} />;
}

export { Badge, badgeVariants };

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-lg text-sm font-medium ring-offset-background transition-all duration-200 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90 hover:shadow-[0_0_20px_hsl(var(--primary)/0.4)]",
        destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline: "border border-border bg-transparent hover:bg-secondary hover:border-primary/50",
        secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-secondary hover:text-foreground",
        link: "text-primary underline-offset-4 hover:underline",
        // NotFox custom variants
        glow: "bg-primary text-primary-foreground hover:bg-primary/90 shadow-[0_0_20px_hsl(var(--primary)/0.3)] hover:shadow-[0_0_30px_hsl(var(--primary)/0.5)]",
        glass: "bg-card/60 backdrop-blur-xl border border-white/10 text-foreground hover:bg-card/80 hover:border-primary/30",
        hero: "bg-gradient-to-r from-primary to-accent text-primary-foreground font-semibold hover:shadow-[0_0_40px_hsl(var(--primary)/0.5)] hover:scale-105",
        icon: "bg-secondary/50 hover:bg-secondary text-muted-foreground hover:text-foreground",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-lg px-8",
        xl: "h-14 rounded-xl px-10 text-base",
        icon: "h-10 w-10",
        "icon-sm": "h-8 w-8",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return <Comp className={cn(buttonVariants({ variant, size, className }))} ref={ref} {...props} />;
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };

import * as React from "react";
import { type DialogProps } from "@radix-ui/react-dialog";
import { Command as CommandPrimitive } from "cmdk";
import { Search } from "lucide-react";

import { cn } from "@/lib/utils";
import { Dialog, DialogContent } from "@/components/ui/dialog";

const Command = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive>
>(({ className, ...props }, ref) => (
  <CommandPrimitive
    ref={ref}
    className={cn(
      "flex h-full w-full flex-col overflow-hidden rounded-md bg-popover text-popover-foreground",
      className,
    )}
    {...props}
  />
));
Command.displayName = CommandPrimitive.displayName;

interface CommandDialogProps extends DialogProps {}

const CommandDialog = ({ children, ...props }: CommandDialogProps) => {
  return (
    <Dialog {...props}>
      <DialogContent className="overflow-hidden p-0 shadow-lg">
        <Command className="[&_[cmdk-group-heading]]:px-2 [&_[cmdk-group-heading]]:font-medium [&_[cmdk-group-heading]]:text-muted-foreground [&_[cmdk-group]:not([hidden])_~[cmdk-group]]:pt-0 [&_[cmdk-group]]:px-2 [&_[cmdk-input-wrapper]_svg]:h-5 [&_[cmdk-input-wrapper]_svg]:w-5 [&_[cmdk-input]]:h-12 [&_[cmdk-item]]:px-2 [&_[cmdk-item]]:py-3 [&_[cmdk-item]_svg]:h-5 [&_[cmdk-item]_svg]:w-5">
          {children}
        </Command>
      </DialogContent>
    </Dialog>
  );
};

const CommandInput = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Input>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Input>
>(({ className, ...props }, ref) => (
  <div className="flex items-center border-b px-3" cmdk-input-wrapper="">
    <Search className="mr-2 h-4 w-4 shrink-0 opacity-50" />
    <CommandPrimitive.Input
      ref={ref}
      className={cn(
        "flex h-11 w-full rounded-md bg-transparent py-3 text-sm outline-none placeholder:text-muted-foreground disabled:cursor-not-allowed disabled:opacity-50",
        className,
      )}
      {...props}
    />
  </div>
));

CommandInput.displayName = CommandPrimitive.Input.displayName;

const CommandList = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.List>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.List>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.List
    ref={ref}
    className={cn("max-h-[300px] overflow-y-auto overflow-x-hidden", className)}
    {...props}
  />
));

CommandList.displayName = CommandPrimitive.List.displayName;

const CommandEmpty = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Empty>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Empty>
>((props, ref) => <CommandPrimitive.Empty ref={ref} className="py-6 text-center text-sm" {...props} />);

CommandEmpty.displayName = CommandPrimitive.Empty.displayName;

const CommandGroup = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Group>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Group>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Group
    ref={ref}
    className={cn(
      "overflow-hidden p-1 text-foreground [&_[cmdk-group-heading]]:px-2 [&_[cmdk-group-heading]]:py-1.5 [&_[cmdk-group-heading]]:text-xs [&_[cmdk-group-heading]]:font-medium [&_[cmdk-group-heading]]:text-muted-foreground",
      className,
    )}
    {...props}
  />
));

CommandGroup.displayName = CommandPrimitive.Group.displayName;

const CommandSeparator = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Separator>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Separator>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Separator ref={ref} className={cn("-mx-1 h-px bg-border", className)} {...props} />
));
CommandSeparator.displayName = CommandPrimitive.Separator.displayName;

const CommandItem = React.forwardRef<
  React.ElementRef<typeof CommandPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof CommandPrimitive.Item>
>(({ className, ...props }, ref) => (
  <CommandPrimitive.Item
    ref={ref}
    className={cn(
      "relative flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none data-[disabled=true]:pointer-events-none data-[selected='true']:bg-accent data-[selected=true]:text-accent-foreground data-[disabled=true]:opacity-50",
      className,
    )}
    {...props}
  />
));

CommandItem.displayName = CommandPrimitive.Item.displayName;

const CommandShortcut = ({ className, ...props }: React.HTMLAttributes<HTMLSpanElement>) => {
  return <span className={cn("ml-auto text-xs tracking-widest text-muted-foreground", className)} {...props} />;
};
CommandShortcut.displayName = "CommandShortcut";

export {
  Command,
  CommandDialog,
  CommandInput,
  CommandList,
  CommandEmpty,
  CommandGroup,
  CommandItem,
  CommandShortcut,
  CommandSeparator,
};

import * as React from "react";
import * as LabelPrimitive from "@radix-ui/react-label";
import { Slot } from "@radix-ui/react-slot";
import { Controller, ControllerProps, FieldPath, FieldValues, FormProvider, useFormContext } from "react-hook-form";

import { cn } from "@/lib/utils";
import { Label } from "@/components/ui/label";

const Form = FormProvider;

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
> = {
  name: TName;
};

const FormFieldContext = React.createContext<FormFieldContextValue>({} as FormFieldContextValue);

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>,
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  );
};

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext);
  const itemContext = React.useContext(FormItemContext);
  const { getFieldState, formState } = useFormContext();

  const fieldState = getFieldState(fieldContext.name, formState);

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>");
  }

  const { id } = itemContext;

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  };
};

type FormItemContextValue = {
  id: string;
};

const FormItemContext = React.createContext<FormItemContextValue>({} as FormItemContextValue);

const FormItem = React.forwardRef<HTMLDivElement, React.HTMLAttributes<HTMLDivElement>>(
  ({ className, ...props }, ref) => {
    const id = React.useId();

    return (
      <FormItemContext.Provider value={{ id }}>
        <div ref={ref} className={cn("space-y-2", className)} {...props} />
      </FormItemContext.Provider>
    );
  },
);
FormItem.displayName = "FormItem";

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField();

  return <Label ref={ref} className={cn(error && "text-destructive", className)} htmlFor={formItemId} {...props} />;
});
FormLabel.displayName = "FormLabel";

const FormControl = React.forwardRef<React.ElementRef<typeof Slot>, React.ComponentPropsWithoutRef<typeof Slot>>(
  ({ ...props }, ref) => {
    const { error, formItemId, formDescriptionId, formMessageId } = useFormField();

    return (
      <Slot
        ref={ref}
        id={formItemId}
        aria-describedby={!error ? `${formDescriptionId}` : `${formDescriptionId} ${formMessageId}`}
        aria-invalid={!!error}
        {...props}
      />
    );
  },
);
FormControl.displayName = "FormControl";

const FormDescription = React.forwardRef<HTMLParagraphElement, React.HTMLAttributes<HTMLParagraphElement>>(
  ({ className, ...props }, ref) => {
    const { formDescriptionId } = useFormField();

    return <p ref={ref} id={formDescriptionId} className={cn("text-sm text-muted-foreground", className)} {...props} />;
  },
);
FormDescription.displayName = "FormDescription";

const FormMessage = React.forwardRef<HTMLParagraphElement, React.HTMLAttributes<HTMLParagraphElement>>(
  ({ className, children, ...props }, ref) => {
    const { error, formMessageId } = useFormField();
    const body = error ? String(error?.message) : children;

    if (!body) {
      return null;
    }

    return (
      <p ref={ref} id={formMessageId} className={cn("text-sm font-medium text-destructive", className)} {...props}>
        {body}
      </p>
    );
  },
);
FormMessage.displayName = "FormMessage";

export { useFormField, Form, FormItem, FormLabel, FormControl, FormDescription, FormMessage, FormField };

import * as React from "react";
import * as NavigationMenuPrimitive from "@radix-ui/react-navigation-menu";
import { cva } from "class-variance-authority";
import { ChevronDown } from "lucide-react";

import { cn } from "@/lib/utils";

const NavigationMenu = React.forwardRef<
  React.ElementRef<typeof NavigationMenuPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Root>
>(({ className, children, ...props }, ref) => (
  <NavigationMenuPrimitive.Root
    ref={ref}
    className={cn("relative z-10 flex max-w-max flex-1 items-center justify-center", className)}
    {...props}
  >
    {children}
    <NavigationMenuViewport />
  </NavigationMenuPrimitive.Root>
));
