import {
  useState,
  useEffect,
  createContext,
  useContext,
  ReactNode,
} from "react";
import { User, Session } from "@supabase/supabase-js";
import { supabase } from "@/integrations/supabase/client";

interface Profile {
  id: string;
  user_id: string;
  username: string;
  display_name: string | null;
  avatar_url: string | null;
  banner_url: string | null;
  bio: string | null;
  status: "online" | "idle" | "dnd" | "offline";
  custom_status: string | null;
  subscription_tier: "free" | "nitro" | "nitro_boost";
  is_verified: boolean;
  github_url: string | null;
  twitter_url: string | null;
  portfolio_url: string | null;
}

interface AuthContextType {
  user: User | null;
  session: Session | null;
  profile: Profile | null;
  loading: boolean;
  signUp: (
    email: string,
    password: string,
    username: string
  ) => Promise<{ error: Error | null }>;
  signIn: (email: string, password: string) => Promise<{ error: Error | null }>;
  signOut: () => Promise<void>;
  updateProfile: (
    updates: Partial<Profile>
  ) => Promise<{ error: Error | null }>;
  updateStatus: (status: Profile["status"]) => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [profile, setProfile] = useState<Profile | null>(null);
  const [loading, setLoading] = useState(true);

  const fetchProfile = async (userId: string) => {
    const { data, error } = await supabase
      .from("profiles")
      .select("*")
      .eq("user_id", userId)
      .maybeSingle();

    if (error) {
      console.error("Error fetching profile:", error);
      return null;
    }
    return data as Profile | null;
  };

  useEffect(() => {
    supabase.auth.onAuthStateChange((event, session) => {
      setSession(session);
      setUser(session?.user ?? null);

      if (session?.user) {
        setTimeout(async () => {
          const profileData = await fetchProfile(session.user.id);
          setProfile(profileData);
          setLoading(false);
        }, 0);
      } else {
        setProfile(null);
        setLoading(false);
      }
    });

    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);

      if (session?.user) {
        fetchProfile(session.user.id).then((profileData) => {
          setProfile(profileData);
          setLoading(false);
        });
      } else {
        setLoading(false);
      }
    });
  }, []);

  const signUp = async (email: string, password: string, username: string) => {
    const { api } = await import("@/services/api");
    try {
      const { user, session, error } = await api.auth.register({
        email,
        password,
        username,
      });
      if (error) throw new Error(error);
      if (session) await supabase.auth.setSession(session);
      return { error: null };
    } catch (e: any) {
      return { error: e };
    }
  };

  const signIn = async (email: string, password: string) => {
    const { api } = await import("@/services/api");
    try {
      const { user, session, error } = await api.auth.login({
        email,
        password,
      });
      if (error) throw new Error(error);
      if (session) await supabase.auth.setSession(session);
      return { error: null };
    } catch (e: any) {
      return { error: e };
    }
  };

  const signOut = async () => {
    const { api } = await import("@/services/api");
    await api.auth.logout();
    await supabase.auth.signOut();
    setUser(null);
    setSession(null);
    setProfile(null);
  };

  const updateProfile = async (updates: Partial<Profile>) => {
    if (!profile) return { error: new Error("No profile found") };

    const { error } = await supabase
      .from("profiles")
      .update(updates)
      .eq("id", profile.id);

    if (!error) {
      setProfile({ ...profile, ...updates });
    }

    return { error: error as Error | null };
  };

  const updateStatus = async (status: Profile["status"]) => {
    if (!profile) return;

    await supabase.from("profiles").update({ status }).eq("id", profile.id);

    setProfile({ ...profile, status });
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        session,
        profile,
        loading,
        signUp,
        signIn,
        signOut,
        updateProfile,
        updateStatus,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "./useAuth";
import { useToast } from "@/hooks/use-toast";

import { Server, Channel } from "@/types";
export type { Server, Channel };

export const useServers = () => {
  const { profile } = useAuth();
  const { toast } = useToast();
  const [servers, setServers] = useState<Server[]>([]);
  const [channels, setChannels] = useState<Channel[]>([]);
  const [loading, setLoading] = useState(true);

  const fetchServers = async () => {
    if (!profile) return;
    try {
      const { api } = await import("@/services/api");
      const data = await api.servers.list();
      setServers(data || []);
    } catch (error) {
      console.error("Error fetching servers:", error);
    }
    setLoading(false);
  };

  const fetchChannels = async (serverId: string) => {
    try {
      const { api } = await import("@/services/api");
      const data = await api.servers.getChannels(serverId);

      setChannels(data || []);
    } catch (error) {
      console.error("Error fetching channels:", error);
    }
  };

  const createServer = async (name: string, description?: string) => {
    if (!profile) return null;

    try {
      const { api } = await import("@/services/api");
      const server = await api.servers.create({ name, description });

      if (server.error) throw new Error(server.error);

      await fetchServers();
      return server as Server;
    } catch (error: any) {
      toast({
        title: "Error creating server",
        description: error.message,
        variant: "destructive",
      });
      return null;
    }
  };

  const joinServer = async (inviteCode: string) => {
    if (!profile) return null;

    try {
      const { api } = await import("@/services/api");
      const server = await api.servers.join(inviteCode);

      if (server.error) throw new Error(server.error);

      toast({
        title: "Joined server!",
        description: `You are now a member of ${server.name}`,
      });

      await fetchServers();
      return server as Server;
    } catch (error: any) {
      toast({
        title: "Error joining server",
        description: error.message || "Failed to join server",
        variant: "destructive",
      });
      return error.server ? (error.server as Server) : null;
    }
  };

  const createChannel = async (
    serverId: string,
    name: string,
    type: Channel["type"] = "text"
  ) => {
    try {
      const { api } = await import("@/services/api");
      const channel = await api.channels.create({ serverId, name, type });

      if (channel.error) throw new Error(channel.error);

      await fetchChannels(serverId);
      return channel as Channel;
    } catch (error: any) {
      toast({
        title: "Error creating channel",
        description: error.message,
        variant: "destructive",
      });
      return null;
    }
  };

  useEffect(() => {
    if (profile) {
      fetchServers();
    }
  }, [profile]);

  return {
    servers,
    channels,
    loading,
    fetchServers,
    fetchChannels,
    createServer,
    joinServer,
    createChannel,
  };
};

import { useEffect, useRef, useState, useCallback } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from './use-toast';
import { RealtimeChannel } from '@supabase/supabase-js';

export const useWebRTC = (conversationId: string | undefined, userId: string | undefined, options: { isVideo?: boolean } = {}) => {
    const [localStream, setLocalStream] = useState<MediaStream | null>(null);
    const [remoteStreams, setRemoteStreams] = useState<Map<string, MediaStream>>(new Map());
    const [peers, setPeers] = useState<string[]>([]);


    const peerConnections = useRef<Map<string, RTCPeerConnection>>(new Map());
    const localStreamRef = useRef<MediaStream | null>(null);
    const channelRef = useRef<RealtimeChannel | null>(null);

    const { toast } = useToast();


    const cleanup = useCallback(() => {


        if (localStreamRef.current) {
            localStreamRef.current.getTracks().forEach(track => track.stop());
            localStreamRef.current = null;
        }

        peerConnections.current.forEach((pc) => pc.close());
        peerConnections.current.clear();

        if (channelRef.current) {

            channelRef.current.unsubscribe();
            channelRef.current = null;
        }

        setLocalStream(null);
        setRemoteStreams(new Map());
        setPeers([]);
    }, []);


    const createPeerConnection = useCallback(async (targetUserId: string, initiator: boolean) => {
        if (peerConnections.current.has(targetUserId)) {
            return peerConnections.current.get(targetUserId);
        }



        const pc = new RTCPeerConnection({
            iceServers: [
                { urls: 'stun:stun.l.google.com:19302' },
                { urls: 'stun:global.stun.twilio.com:3478' }
            ]
        });


        if (localStreamRef.current) {
            localStreamRef.current.getTracks().forEach(track => {
                pc.addTrack(track, localStreamRef.current!);
            });
        }


        pc.onicecandidate = (event) => {
            if (event.candidate && channelRef.current) {
                channelRef.current.send({
                    type: 'broadcast',
                    event: 'candidate',
                    payload: {
                        candidate: event.candidate,
                        targetUserId: targetUserId,
                        fromUserId: userId
                    }
                });
            }
        };


        pc.ontrack = (event) => {

            setRemoteStreams(prev => {
                const newMap = new Map(prev);
                newMap.set(targetUserId, event.streams[0]);
                return newMap;
            });
        };

        pc.onconnectionstatechange = () => {

            if (pc.connectionState === 'failed' || pc.connectionState === 'closed') {
                peerConnections.current.delete(targetUserId);
                setRemoteStreams(prev => {
                    const newMap = new Map(prev);
                    newMap.delete(targetUserId);
                    return newMap;
                });
            }
        };

        peerConnections.current.set(targetUserId, pc);

        if (initiator && channelRef.current) {
            try {
                const offer = await pc.createOffer();
                await pc.setLocalDescription(offer);
                await channelRef.current.send({
                    type: 'broadcast',
                    event: 'offer',
                    payload: {
                        offer,
                        targetUserId,
                        fromUserId: userId
                    }
                });
            } catch (err) {
                console.error("Error creating offer:", err);
            }
        }

        return pc;
    }, [userId]);


    useEffect(() => {
        if (!conversationId || !userId) return;


        if (channelRef.current) channelRef.current.unsubscribe();

        const channel = supabase.channel(`signaling:${conversationId}`, {
            config: {
                presence: {
                    key: userId,
                },
            },
        });
        channelRef.current = channel;

        const init = async () => {
            try {

                const stream = await navigator.mediaDevices.getUserMedia({
                    audio: true,
                    video: options.isVideo ?? false
                });

                localStreamRef.current = stream;
                setLocalStream(stream);


                channel
                    .on('presence', { event: 'sync' }, async () => {
                        const state = channel.presenceState();

                        const onlineUsers = [];
                        for (const key in state) {
                            onlineUsers.push(...state[key]); // state[key] is array of presences
                        }

                        const currentPeerIds = onlineUsers
                            .map((u: any) => u.user_id || u.key) // Fallback if user_id missing, though we sent key
                            .filter(id => id && id !== userId);

                        const uniquePeers = [...new Set(currentPeerIds)];

                        setPeers(uniquePeers as string[]);


                        for (const peerId of uniquePeers as string[]) {
                            if (!peerConnections.current.has(peerId)) {
                                if (userId < peerId) {

                                    await createPeerConnection(peerId, true);
                                }
                            }
                        }
                    })
                    .on('presence', { event: 'join' }, ({ newPresences }) => {


                    })
                    .on('presence', { event: 'leave' }, ({ leftPresences }) => {
                        leftPresences.forEach((user: any) => {
                            const id = user.user_id || user.key; // Verify field
                            if (id) {

                                const pc = peerConnections.current.get(id);
                                if (pc) {
                                    pc.close();
                                    peerConnections.current.delete(id);
                                    setRemoteStreams(prev => {
                                        const m = new Map(prev);
                                        m.delete(id);
                                        return m;
                                    });
                                }
                            }
                        });
                    })
                    .on('broadcast', { event: 'offer' }, async ({ payload }) => {
                        if (payload.targetUserId !== userId) return;


                        const pc = await createPeerConnection(payload.fromUserId, false);
                        if (pc) {
                            if (pc.signalingState !== 'stable') {

                            }
                            await pc.setRemoteDescription(new RTCSessionDescription(payload.offer));
                            const answer = await pc.createAnswer();
                            await pc.setLocalDescription(answer);

                            channel.send({
                                type: 'broadcast',
                                event: 'answer',
                                payload: {
                                    answer,
                                    targetUserId: payload.fromUserId,
                                    fromUserId: userId
                                }
                            });
                        }
                    })
                    .on('broadcast', { event: 'answer' }, async ({ payload }) => {
                        if (payload.targetUserId !== userId) return;

                        const pc = peerConnections.current.get(payload.fromUserId);
                        if (pc) {
                            await pc.setRemoteDescription(new RTCSessionDescription(payload.answer));
                        }
                    })
                    .on('broadcast', { event: 'candidate' }, async ({ payload }) => {
                        if (payload.targetUserId !== userId) return;
                        const pc = peerConnections.current.get(payload.fromUserId);
                        if (pc) {
                            try {
                                await pc.addIceCandidate(new RTCIceCandidate(payload.candidate));
                            } catch (e) {
                                console.error("ICE Error", e);
                            }
                        }
                    });

                // Subscribe
                channel.subscribe(async (status) => {
                    if (status === 'SUBSCRIBED') {

                        await channel.track({ user_id: userId, online_at: new Date().toISOString() });
                    }
                });

            } catch (err) {
                console.error("WebRTC Init Error:", err);
                toast({ title: "Error", description: "Failed to access media or connect.", variant: "destructive" });
            }
        };

        init();

        return () => cleanup();
    }, [conversationId, userId, createPeerConnection, cleanup, options.isVideo, toast]);


    const toggleMic = useCallback(() => {
        if (localStreamRef.current) {
            localStreamRef.current.getAudioTracks().forEach(t => t.enabled = !t.enabled);
            return !localStreamRef.current.getAudioTracks()[0].enabled;
        }
        return false;
    }, []);

    const toggleVideo = useCallback(() => {
        if (localStreamRef.current) {
            localStreamRef.current.getVideoTracks().forEach(t => t.enabled = !t.enabled);
            return !localStreamRef.current.getVideoTracks()[0].enabled;
        }
        return false;
    }, []);

    return {
        localStream,
        remoteStreams,
        peers,
        toggleMic,
        toggleVideo
    };
};

