          description: data.message,
        });
      } catch (error) {
        console.error("Backend connection failed:", error);
        toast({
          variant: "destructive",
          title: "Backend Error",
          description: "Could not connect to backend server",
        });
      }
    };
    checkHealth();
  }, []);

  useEffect(() => {
    if (servers.length > 0 && !activeServerId && viewMode === "server") {
      setActiveServerId(servers[0].id);
    }
  }, [servers, activeServerId, viewMode]);

  useEffect(() => {
    if (activeServerId) {
      fetchChannels(activeServerId);
    }
  }, [activeServerId]);

  useEffect(() => {
    if (channels.length > 0 && activeServerId) {
      const textChannel = channels.find((c) => c.type === "text");
      if (textChannel) {
        setActiveChannelId(textChannel.id);
      }
    }
  }, [channels, activeServerId]);

  useEffect(() => {
    const fetchMembers = async () => {
      if (!activeServerId) return;

      const { data } = await supabase
        .from("server_members")
        .select(
          `
          id,
          nickname,
          profile:profiles(*)
        `
        )
        .eq("server_id", activeServerId);

      setMembers(
        data?.map((m) => ({ ...m.profile, nickname: m.nickname })) || []
      );
    };

    fetchMembers();
  }, [activeServerId]);

  const handleServerSelect = (serverId: string) => {
    if (serverId === "home") {
      setViewMode("dm");
      setActiveServerId(null);
      return;
    }
    setViewMode("server");
    setActiveServerId(serverId);
    setActiveChannelId(null);
  };

  const handleCreateServer = async (name: string, description?: string) => {
    const server = await createServer(name, description);
    if (server) {
      setActiveServerId(server.id);
      setShowCreateServer(false);
      toast({
        title: "Server created!",
        description: `${name} is ready to go.`,
      });
    }
  };

  const handleJoinServer = async (inviteCode: string) => {
    const server = await joinServer(inviteCode);
    if (server) {
      setActiveServerId(server.id);
      setShowJoinServer(false);
    }
  };

  // Show landing page if not logged in
  if (!authLoading && !user) {
    return (
      <LandingPage
        onEnterApp={() => navigate("/auth")}
        onOpenNitro={() => setShowNitro(true)}
      />
    );
  }

  // Loading state
  if (authLoading || serversLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="flex flex-col items-center gap-4"
        >
          <div className="w-16 h-16 rounded-2xl bg-primary glow flex items-center justify-center text-3xl animate-pulse">
            ≡ƒªè
          </div>
          <p className="text-muted-foreground">Loading NotFox...</p>
        </motion.div>
      </div>
    );
  }

  const activeServer = servers.find((s) => s.id === activeServerId);
  const activeChannel = channels.find((c) => c.id === activeChannelId);

  return (
    <>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        className="flex h-screen bg-background overflow-hidden"
      >
        {/* Server Sidebar - Hidden on mobile, shown on lg+ screens */}
        <div className="hidden lg:flex flex-shrink-0">
          <ServerSidebar
            servers={servers}
            activeServerId={activeServerId || ""}
            onServerSelect={handleServerSelect}
            onAddServer={() => setShowCreateServer(true)}
            onJoinServer={() => setShowJoinServer(true)}
          />
        </div>

        {/* Define the mobile menu renderer */}
        {(() => {
          const mobileMenu = (
            <MobileSidebar
              servers={servers}
              activeServerId={activeServerId}
              onServerSelect={handleServerSelect}
              onAddServer={() => setShowCreateServer(true)}
              onJoinServer={() => setShowJoinServer(true)}
              viewMode={viewMode}
              activeServer={activeServer}
              channels={channels}
              activeChannelId={activeChannelId}
              onChannelSelect={setActiveChannelId}
              createChannel={createChannel}
              setShowSettings={setShowSettings}
              profile={profile}
              setActiveVoiceChannel={setActiveVoiceChannel}
              activeConversationId={activeConversationId}
              setActiveConversationId={setActiveConversationId}
            />
          );

          return (
            <>
              {/* Channel Sidebar - Hidden on mobile, shown on lg+ screens */}
              {viewMode === "server" && activeServer && (
                <div className="hidden lg:block h-full">
                  <AnimatePresence mode="wait">
                    <motion.div
                      key={activeServerId}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: -20 }}
                      transition={{ duration: 0.2 }}
                      className="h-full"
                    >
                      <ChannelSidebarReal
                        server={activeServer}
                        channels={channels}
                        activeChannelId={activeChannelId || ""}
                        onChannelSelect={setActiveChannelId}
                        onCreateChannel={(name, type) =>
                          createChannel(activeServerId!, name, type)
                        }
                        onOpenSettings={() => setShowSettings(true)}
                        profile={profile!}
                        onJoinVoice={setActiveVoiceChannel}
                      />
                    </motion.div>
                  </AnimatePresence>
                </div>
              )}

              {/* DM List Sidebar - Hidden on mobile, shown on lg+ screens */}
              {viewMode === "dm" && (
                <div className="hidden lg:block h-full">
                  <DMList
                    activeConversationId={activeConversationId}
                    onSelectConversation={setActiveConversationId}
                  />
                </div>
              )}

              {/* Chat Area - Responsive */}
              <div className="flex-1 min-w-0 flex flex-col h-full overflow-hidden">
                {viewMode === "server" ? (
                  activeChannel ? (
                    <ChatAreaReal
                      channel={activeChannel}
                      renderMobileMenu={mobileMenu}
                    />
                  ) : activeServer ? (
                    <div className="flex-1 flex items-center justify-center bg-background">
                      <div className="text-center p-4">
                        {mobileMenu}
                        <div className="text-6xl mb-4">≡ƒªè</div>
                        <h2 className="text-2xl font-display text-foreground mb-2">
                          Select a Channel
                        </h2>
                        <p className="text-muted-foreground max-w-md mx-auto">
                          Choose a channel from the sidebar to start chatting.
                        </p>
                      </div>
                    </div>
                  ) : (
                    <div className="flex-1 flex items-center justify-center bg-background">
                      <div className="text-center p-4">
                        {mobileMenu}
                        <div className="text-6xl mb-4">≡ƒªè</div>
                        <h2 className="text-2xl font-display text-foreground mb-2">
                          Create Your First Server
                        </h2>
                        <p className="text-muted-foreground max-w-md mx-auto">
                          Start by creating a server or joining one.
                        </p>
                        <div className="flex flex-wrap gap-3 justify-center mt-6">
                          <button
                            onClick={() => setShowCreateServer(true)}
                            className="px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors"
                          >
                            Create Server
                          </button>
                          <button
                            onClick={() => setShowJoinServer(true)}
                            className="px-4 py-2 bg-secondary text-foreground rounded-lg hover:bg-secondary/80 transition-colors"
                          >
                            Join Server
                          </button>
                        </div>
                      </div>
                    </div>
                  )
                ) : activeConversationId === "friends" ? (
                  <FriendList renderMobileMenu={mobileMenu} />
                ) : activeConversationId ? (
                  <DMArea
                    conversationId={activeConversationId}
                    renderMobileMenu={mobileMenu}
                  />
                ) : (
                  <div className="flex-1 flex items-center justify-center bg-background">
                    <div className="text-center p-4">
                      {mobileMenu}
                      <div className="text-6xl mb-4">≡ƒÆ¼</div>
                      <h2 className="text-2xl font-display text-foreground mb-2">
                        Your Messages
                      </h2>
                      <p className="text-muted-foreground max-w-md mx-auto">
                        Select a conversation or start a new one.
                      </p>
                    </div>
                  </div>
                )}
              </div>

              {/* Member Sidebar - Hidden on mobile/tablets, shown on xl+ screens */}
              {viewMode === "server" && activeChannel && members.length > 0 && (
                <div className="hidden xl:block h-full">
                  <MemberSidebarReal
                    members={members}
                    serverId={activeServerId!}
                  />
                </div>
              )}
            </>
          );
        })()}
      </motion.div>

      <CreateServerModal
        isOpen={showCreateServer}
        onClose={() => setShowCreateServer(false)}
        onCreate={handleCreateServer}
      />

      <JoinServerModal
        isOpen={showJoinServer}
        onClose={() => setShowJoinServer(false)}
        onJoin={handleJoinServer}
      />

      <NitroModal isOpen={showNitro} onClose={() => setShowNitro(false)} />

      <UserSettingsModal
        isOpen={showSettings}
        onClose={() => setShowSettings(false)}
        onSignOut={signOut}
      />

      {activeVoiceChannel && (
        <CallModal
          isOpen={!!activeVoiceChannel}
          onClose={() => setActiveVoiceChannel(null)}
          participant={{
            username: activeVoiceChannel.name,
            display_name: activeVoiceChannel.name,
            avatar_url: null,
          }}
          conversationId={activeVoiceChannel.id}
          isVideo={activeVoiceChannel.type === "video"}
        />
      )}
    </>
  );
};

export default Index;
